SET QUOTED_IDENTIFIER ON
GO
CREATE FUNCTION [dbo].[Convertir_Horas] (@lfHoras decimal(8, 4)) returns varchar(5)  AS BEGIN   declare @minutos int   declare @horas int   declare @horx varchar(2)   declare @minx varchar(2)     select @horas = Round(@lfHoras, 0,1)   select @minutos = Round((@lfHoras - @horas) * 60, 0)   If @minutos = 60   Begin     set @minutos = 0     set @horas = @horas + 1   End   select @horx = convert(varchar(2),@horas)   select @minx = convert(varchar(2),@minutos)   return REPLICATE('0',2-LEN(@horx)) + @horx + ':' + REPLICATE('0',2-LEN(@minx)) + @minx END
GO
