CREATE FUNCTION [dbo].[HoraAEntero] (@horaS varchar(5)) returns int  AS BEGIN  Declare @hora int  Declare @min int  Declare @horaEntero int   select @hora = convert(int,substring(@horaS,1,2))  select @min = convert(int,substring(@horaS,4,2))  select @horaEntero = ((@hora * 60) + @min)  Return @horaEntero END
