CREATE PROCEDURE [dbo].[insertarmarca] @legajo char(12), @dia datetime, @hora char(5),  @reloj char(16),@tipo char(1), @toleran int as begin   declare @existe int, @exists int, @codigo int,@estado char(1),@pnid int,@horamin char(5),@horamax char(5),@hormin int, @hormax int   declare @horx varchar(5), @minx varchar(5)   select @hormin =  convert(int,substring(@hora,1,2))*60 + convert(int,substring(@hora,4,2)) - @toleran   if @hormin > 0   begin     select @horx= convert(varchar(5),@hormin/60)     select @horx= rtrim(ltrim(@horx))     select @minx = convert(varchar(5),@hormin%60)     select @minx = rtrim(ltrim(@minx))     select @horamin = REPLICATE('0',2-LEN(@horx)) + @horx + ':' + REPLICATE('0',2-LEN(@minx)) + @minx   end   else     SELECT @horamin = '00:00'    select @hormax =  convert(int,substring(@hora,1,2))*60 + convert(int,substring(@hora,4,2)) + @toleran   if @hormax < 1440   begin     select @horx = convert(char,@hormax/60)     select @horx = rtrim(ltrim(@horx))     select @minx = convert(char,@hormax%60)     select @minx = rtrim(ltrim(@minx))     select @horamax = REPLICATE('0',2 - LEN(@horx)) + @horx + ':' + REPLICATE('0',2 - LEN(@minx)) + @minx     end   else     SELECT @horamax = '23:59'   if @tipo = 'E'       begin     if not exists (select * from tbcasapa where apalegajo = @legajo      and apafecha = @dia      and apahorae between @horamin and @horamax)     begin       select @exists = min(apacodigo) from tbcasapa       where apalegajo =@legajo       and apafecha = @dia       and apaestado = 1       and apahoras >= @hora       OR       apalegajo = @legajo       and apafecha = dateadd(day,1,@dia)       and apaestado = 1       and apahoras < @hora     if @exists is null     begin       select @pnid = 0       exec sp_ww_newid 'tbcasapa',@pnid output       insert into tbcasapa (apacodigo,apalegajo,apafecha,apahorae,apaestado,aparele,apatipo)         values(@pnid,@legajo,@dia,@hora,2,@reloj,'A')     end     else     begin         update tbcasapa set apahorae = @hora, apaestado=0, aparele=@reloj          where apacodigo = @exists     end    end     end   else   begin     -- es una salida  buscar ultima entrada antes de esta salida en mismo dia o anterior     select @codigo = max(apacodigo) from tbcasapa       where apalegajo=@legajo and apahorae >='00:00'       and (apafecha=@dia and apahorae <= @hora or apafecha = dateadd(day,-1,@dia) and apahorae > @hora)       -- se encontro ultima entrada antes de esta salida. Le falta salida     if @codigo is not null     begin       select @estado=apaestado from tbcasapa where apacodigo=@codigo       if @estado = 2          update tbcasapa set apahoras =@hora, apaestado = 0, aparels = @reloj where apacodigo=@codigo       else         if not exists(select apacodigo from tbcasapa where apalegajo =@legajo and           (apafecha=@dia and apahoras between @horamin and @horamax or           apafecha = dateadd(day,-1,@dia) and apahoras between @horamin and @horamax and apahorae > @hora))         begin           select @pnid = 0           exec sp_ww_newid 'tbcasapa',@pnid output           insert into tbcasapa (apacodigo,apalegajo,apafecha,apahoras,apaestado,aparels,apatipo)             values(@pnid,@legajo,@dia,@hora,1,@reloj,'A')         end     end      -- no hay ninguna otra entrada, insertar la salida derechosi asi funco     else       if not exists(select apacodigo from tbcasapa where apalegajo =@legajo and          (apafecha=@dia AND apahoras BETWEEN @horamin AND @horamax                  or         apafecha = dateadd(day,-1,@dia) AND apahoras BETWEEN @horamin AND @horamax and apahorae >@hora))       BEGIN         exec sp_ww_newid 'tbcasapa',@pnid output         INSERT INTO tbcasapa           (apacodigo, apalegajo, apafecha, apahoras, apaestado, aparels, apatipo)           VALUES (@pnid, @legajo, @dia, @hora, 1, @reloj, 'A')       end   -- HASTA ACA ES UNA SALIDA   END END
