CREATE PROCEDURE [dbo].[aparear] @toleran int as begin   declare casrel cursor for select r.relcodigo,r.rellegajo,r.relfecha,r.relhora,r.reltipomov,r.relreloj from tbcasrel r   inner join remples s on r.rellegajo = s.codigo   order by relcodigo    declare @codigo int   declare @legajo char(12)   declare @fecha datetime   declare @hora char(5)   declare @reloj char(5)   declare @cuenta int   declare @tipomov char(1)    begin transaction   open casrel   fetch next from casrel into @codigo,@legajo,@fecha,@hora,@tipomov,@reloj     while @@fetch_status = 0   begin     exec insertarmarca @legajo,@fecha,@hora,@reloj,@tipomov,@toleran     delete from tbcasrel where relcodigo = @codigo     fetch next from casrel into @codigo,@legajo,@fecha,@hora,@tipomov,@reloj   end    close casrel   deallocate casrel    delete from tbcasrel where relcodigo in   (select relcodigo from tbcasrel left outer join remples on rellegajo = codigo where codigo is null)   commit tran end
GO
